    text = u'你都说了 他是前姐夫'

    subtitle_h = 44
    subtitle_w = len(text.replace(" ", "")) * (subtitle_h + 1)

    im = Image.new("RGB", (subtitle_w, subtitle_h), (0, 0, 0))
    dr = ImageDraw.Draw(im)
    font = ImageFont.truetype('/Library/Fonts/华文黑体.ttf', size=subtitle_h-1)

